import java.util.Base64.Encoder

// get flow file
def flowFile = session.get()
if (!flowFile) return

def username = "medic";
def password = flowFile.getAttribute("county_password");

def credentials = "$username:$password"
def encodedCredentials = Base64.getEncoder().encodeToString(credentials.getBytes())
encodedCredentials = "Basic $encodedCredentials"

flowFile = session.putAttribute(flowFile, "county_password" as String, "")
flowFile = session.putAttribute(flowFile, "basic_auth_token" as String, encodedCredentials.toString())

session.transfer(flowFile, REL_SUCCESS)
